/* 6. Your pseudocode algorithm here
	loop 3 times each line
			loop 9 times and print each integer 3 times

*/

public class NumberString {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i = 0; i < 3; i++)
		{
			for(int j = 0; j < 10; j++)
			{
				System.out.print(j);
				System.out.print(j);
				System.out.print(j);
			}
			
			System.out.println("");
		}

	}

}
